pragma solidity >=0.7.0 <0.9.0;

import "./Bech32.sol";

contract StakeDrop3 {

    using Bech32 for string;

    bytes hrpBytes;
    bytes controlDigitBytes;
    uint dataBytesSize;

    event MagicTxEvent(address sender, uint256 height, string xprtAddress);

    /**
     * @dev Setting the values for bech32 validation attributes
     */
    constructor() {
        // setting bech32 validation attributes
        hrpBytes = "persistence";
        controlDigitBytes = "1";
        dataBytesSize = 38;
    }

    /**
     * @dev emits the event for xprt address
     */
    function MagicTx(string memory xprtAddress) public {
        //check if toChainAddress is valid address
        bool isAddressValid = xprtAddress.isBech32AddressValid(hrpBytes, controlDigitBytes, dataBytesSize);
        //require(isAddressValid == true, "StakeDrop3: Invalid xprt address ");
        emit MagicTxEvent(msg.sender, block.number, xprtAddress);
    }
}
